import React, { useState, useEffect, useMemo } from "react";
import useForm from "react-hook-form";
import NavBar from "../../components/navBar";
import HomeBar from "../../components/homeBar";

import api from "../../services/api";

import "./styles.css";
import exclude from "../../assets/button/excluir.svg";
import enclude from "../../assets/button/plus.svg";
import direction from "../../assets/button/direction.svg";

export default function Dish() {
  const [ingredients, setIngredients] = useState([]);

  const { register, handleSubmit, errors } = useForm();
  const onSubmit = data => {
    const { name, price } = data;
    delete data.name;
    delete data.price;
    const list = Object.keys(data).filter(item => {
      if (data[item] !== false) {
        return item;
      }
    });
    const dish = {
      name,
      price,
      ingredients: list
    };
    console.log(dish);
  };

  const [operation, setOperation] = useState("");
  const [index, setIndex] = useState(null);

  useEffect(() => {
    setDishs(JSON.parse(localStorage.getItem("dishs")));
    setIngredients(JSON.parse(localStorage.getItem("ingredients")));

    setOperation("0");
  }, []);

  return (
    <>
      <HomeBar />
      <div id="containerDish">
        <NavBar />
        <section>
          <div className="frist">
            <span>NOVO</span>

            <input
              className="range"
              type="range"
              id="range"
              max="1"
              min="0"
              value={operation}
              onChange={event => setOperation(event.target.value)}
            />
            <span>EDITAR</span>
          </div>
          <div className="second">
            <form onSubmit={handleSubmit(onSubmit)}>
              <fieldset>
                <legend>Nome</legend>
                <input
                  type="text"
                  placeholder="Nome"
                  name="name"
                  ref={register({ required: true })}
                />
              </fieldset>
              <fieldset>
                <legend>Preço</legend>
                <input
                  type="number"
                  placeholder="Preço"
                  name="price"
                  ref={register({ required: true, min: 0 })}
                />
              </fieldset>
              <fieldset>
                <legend>ingredientes</legend>
                <div id="listIngredient">
                  {ingredients.map(ingre => (
                    <section>
                      <input
                        key={ingre._id}
                        type="checkbox"
                        placeholder={ingre._id}
                        name={ingre._id}
                        value={JSON.stringify(ingre)}
                        ref={register}
                      />

                      <label htmlFor={ingre._id}>{ingre.name}</label>
                    </section>
                  ))}
                </div>
              </fieldset>
              <input type="submit" />
            </form>
          </div>
        </section>
      </div>
    </>
  );
}
