import React,{ useState, useEffect} from "react";
import NavBar from "../../components/navBar";
import HomeBar from "../../components/homeBar";
import "./styles.css"
import  {
  Dialog,
  DialogContent,
  DialogContentText,
  DialogTitle,
  DialogActions

} from "@material-ui/core";


export default function Restaurant() {
 const [openIngredient, setOpenIngredient] = useState(false);
 const [openDish, setOpenDish] = useState(false);
 const [openTable, setOpenTable] = useState(false);
 const [type, setType]= useState("");
 const [permission, setPermission] = useState("");
 useEffect(() => {
   setPermission(JSON.parse(localStorage.getItem("permissions")))
 },[])
 function handleClickOpen(name) {
   setType(name)
  if(type ==="ingredient"){
    setOpenIngredient(true);
  }if (type === "dish") {
    setOpenDish(true);
  } else {
    setOpenTable(true);
  }
};

function handleClose(){
  if(type ==="ingredient"){
    setOpenIngredient(true);
  }if (type === "dish") {
    setOpenDish(true);
  } else {
    setOpenTable(true);
  }
};
const mondalIngredient = (
  <Dialog open={openIngredient} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Classificações de Ingredientes</DialogTitle>
        <DialogContent>
          <DialogContentText>

          </DialogContentText>
          {/* <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email Address"
            type="email"
            fullWidth
          /> */}
        </DialogContent>
        <DialogActions>
          {/* <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            Subscribe
          </Button> */}
        </DialogActions>
      </Dialog>
)
 return (
    <>
      <HomeBar />
      <div id="containerRestaurant">
      <NavBar />
      <div>
        <nav className="options">
          <ul>
            <li>
              <span>Classificações</span>
              <ul>
               <li><button onClick={() => handleClickOpen("dish") }>Pratos</button></li>
                <li><button onClick={() => handleClickOpen("ingredient") }>Ingredientes</button></li>
                <li><button onClick={() => handleClickOpen("table") }>Mesas</button></li>
              </ul>
            </li>
          </ul>
        </nav>
        {mondalIngredient}
      </div>
      </div>

    </>
  );
}
