import React, { useState, useEffect, useMemo } from "react";
import useForm from "react-hook-form";
import NavBar from "../../components/navBar";
import HomeBar from "../../components/homeBar";

import api from "../../services/api";

import "./styles.css";

export default function Dish() {
  const [dishs, setDishs] = useState([]);
  const [ingredients, setIngredients] = useState([]);
  const [operation, setOperation] = useState("");

  const [dish, setDish] = useState("");
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const { register, handleSubmit, errors } = useForm();
  const onSubmit = async data => {
    const { name, price } = data;
    delete data.name;
    delete data.price;

    for (let prop in data) {
      if (data[prop] === false) {
        delete data[prop];
      }
    }
    const arr = Object.values(data);

    api.post("/dish", {
      name,
      price,
      ingredients: arr
    });
  };
  useMemo(() => {
    setName(dish.name);
    setPrice(dish.price);
  }, [dish]);
  const preview = useMemo(() => {
    return operation === "1" ? (
      <select onChange={event => setDish(event.target.value)}>
        <option value="">Escolha um prato</option>
        {dishs.map(item => (
          <option key={item._id} value={JSON.stringify(item)}>
            {item.name}
          </option>
        ))}
      </select>
    ) : (
      <span />
    );
  }, [operation]);
  useEffect(() => {
    setDishs(JSON.parse(localStorage.getItem("dishs")));
    setIngredients(JSON.parse(localStorage.getItem("ingredients")));

    setOperation("0");
  }, []);

  return (
    <>
      <HomeBar />
      <div id="containerDish">
        <NavBar />
        <section>
          <div className="frist">
            <span>NOVO</span>

            <input
              className="range"
              type="range"
              id="range"
              max="1"
              min="0"
              value={operation}
              onChange={event => setOperation(event.target.value)}
            />
            <span>EDITAR</span>
            {preview}
          </div>
          <div className="second">
            <form onSubmit={handleSubmit(onSubmit)}>
              <fieldset>
                <legend>Nome</legend>
                <input
                  type="text"
                  placeholder="Nome"
                  name="name"
                  value={name}
                  ref={register({ required: true })}
                />
              </fieldset>
              <fieldset>
                <legend>Preço</legend>

                {operation === "1" ? (
                  <input
                    type="number"
                    name="price"
                    ref={register({ required: true, min: 0 })}
                    value={name}
                  />
                ) : (
                  <input
                    type="number"
                    name="price"
                    ref={register({ required: true, min: 0 })}
                    placeholder="Preço"
                  />
                )}
              </fieldset>
              <fieldset>
                <legend>ingredientes</legend>
                <div id="listIngredient">
                  <section>
                    {operation === "1" ? (
                      <>
                        {ingredients.map(item =>
                          dish.ingredients.indexOf(item) < -1 ? (
                            <>
                              <input
                                key={item._id}
                                type="checkbox"
                                placeholder={item._id}
                                name={item._id}
                                value={item._id}
                                ref={register}
                                checked
                              />
                              <label htmlFor={item._id}>{item.name}</label>
                            </>
                          ) : (
                            <>
                              <input
                                key={item._id}
                                type="checkbox"
                                placeholder={item._id}
                                name={item._id}
                                value={item._id}
                                ref={register}
                              />

                              <label htmlFor={item._id}>{item.name}</label>
                            </>
                          )
                        )}
                      </>
                    ) : (
                      <>
                        {ingredients.map(item => (
                          <>
                            <input
                              key={item._id}
                              type="checkbox"
                              placeholder={item._id}
                              name={item._id}
                              value={item._id}
                              ref={register}
                            />

                            <label htmlFor={item._id}>{item.name}</label>
                          </>
                        ))}
                      </>
                    )}
                  </section>
                </div>
              </fieldset>
              <input type="submit" />
            </form>
          </div>
        </section>
      </div>
    </>
  );
}
