import React, {useEffect, useState, useMemo} from "react";
import api from "../../services/api";
import NavBar from "../../components/navBar";
import HomeBar from "../../components/homeBar";
import { Modal} from 'react-bootstrap';
import useForm from "react-hook-form";
import 'bootstrap/dist/css/bootstrap.min.css';
import "./styles.css"
import destroy from "../../assets/button/destroy.svg";




export default function Orders() {
  const [dishs, setDishs] = useState([]);
  const [classifications, setClassifications] = useState([]);
  const [category, setCategory] = useState("");
  const [dish, setDish] = useState("")
  const [show, setShow] = useState(false);
  const [ingredients, setIngredients] = useState([])
  const [extra, setExtra] = useState([]);
  const [command, setCommand] = useState([]);
  const { handleSubmit, register, errors } = useForm();




  const onSubmit = async data => {
    const { ps, amount } = data;
    delete data.ps;
    delete data.amount
    let withdraw = []
    let extra = []
    for (let prop in data) {
      if (data[prop] === false) {
        delete data[prop];
      }else{
        dish.ingredients.map(item => {
          if(item._id === data[prop]){
            withdraw.push(data[prop])
            delete data[prop]
          }})
        if(data[prop]){
          extra.push(data[prop])
          delete data[prop]
        }
      }
  }
    const response = await api.post("orders",{
      dish: dish._id,
      withdraw,
      extra,
      ps,
      amount
    })
    let arr = command
    arr.push(response.data)
    console.log(arr)
    setCommand(arr)
    console.log(command)
    handleClose()
  }

  const theCommand = useMemo(() => {

    return !command.length !== 0
    ?(
      <>
        <li>
          <span>Item</span>
          <button><img src={destroy} alt="destroy"/></button>
          <ul>
            <li>
              <span>
                masidfhoasfhasjdfnlsdnfldsfj sajfasjfbasjfj asjofnaojsfnajfn
                afnajofnajsfn asfnaosnfnjaf asnfoanfoaf ajfnpajfipafj oanfioaf
                anofnaifiap
                jaffnfaf
              </span>
            </li>
          </ul>
        </li>
        <li>
          <span>Item</span>
          <button><img src={destroy} alt="destroy"/></button>
          <ul>
            <li>
              <span>
                masidfhoasfhasjdfnlsdnfldsfj sajfasjfbasjfj asjofnaojsfnajfn
                afnajofnajsfn asfnaosnfnjaf asnfoanfoaf ajfnpajfipafj oanfioaf
                anofnaifiap
                jaffnfaf
              </span>
            </li>
          </ul>
        </li>

        {command.map(item => (
          <li key="item.id">
            <button href="">
              {item.dish.name}
              <span>+</span>
            </button>
          </li>
        ))}
        <button>Confirmar</button>
      </>
    )
    :(<span className="empty">Comanda vazia</span>)
  },[command])

  function handleClose(){
    setShow(false);
  }
 function handleShow(item){
   setDish(item)
   setIngredients(item.ingredients)
   setShow(true);
  }

  useEffect(()=>{
    const arr = JSON.parse(localStorage.getItem("dishs"))
    setDishs(arr);
    setClassifications(JSON.parse(localStorage.getItem("classification")));
    let variable = []
    function resolveThis(element, index, array){
    if(element.classification.classification === "Extra"){
      variable.push(element)
    }
  }

  arr.forEach(resolveThis)
  setExtra(variable)
  },[])

  const navClassifications = useMemo(() => {
    return(
      <div id="navClass">
      {classifications.map(item => {
        if(item.section === "dish" && item.classification !== "Extra"){
          return(
            <header key={item._id}>
              <button onClick={() => setCategory(item.classification)}>
                {item.classification}
              </button>
            </header>
          )
        }
      })}

     </div>
    )

  }, [classifications])

  const listDish = useMemo(() => {
    if(category === ""){
      return <h1>Selecione uma categoria</h1>
    }else{
      return(
        <div id="listDish">
          {dishs.map(item => {
            if(item.classification.classification === category){
              return(
                <header key={item._id}>
                  <button onClick={() => handleShow(item)}>
                    {item.name}
                  </button>
                </header>
              )
            }
          })}
        </div>
      )
    }
  },[category])



  const modal = (
      <Modal show={show} onHide={handleClose}>
        <form onSubmit={handleSubmit(onSubmit)}>
          <Modal.Header closeButton>
            <Modal.Title>{dish.name}</Modal.Title>
          </Modal.Header>

          <Modal.Body className="options">
            Selecione os Ingredientes que deseja retirar
            <div>
            {ingredients.map(item => (
            <>
              <input
                key={item._id}
                type="checkbox"
                name={item.name}
                value={item._id}
                ref={register}

              />
              <label htmlFor={item.name}>{item.name}</label>
            </>
          ))}
          </div>
          </Modal.Body>
          <Modal.Body className="options">
            Selecione os extras que deseja adicionar:
            <div>
            {extra.map(item => (
            <>
              <input
                key={item._id}
                type="checkbox"
                name={item.name}
                value={item._id}
                ref={register}

              />
              <label htmlFor={item.name}>{item.name}</label>
            </>
          ))}
          </div>
          </Modal.Body>
          <Modal.Body className="options">
            <label htmlFor="ps">Observações</label><br/>
            <textarea name="ps" ref={register} />
          </Modal.Body>
          <Modal.Footer>
            <label htmlFor="amount">Quantidade</label>
            <input
                type="number"
                name="amount"
                ref={register}
                min="1"
                required
            />
            <button variant="secondary" onClick={handleClose}>
              Close
            </button>
            <button variant="primary" type="submit">
              Save Changes
            </button>
          </Modal.Footer>
        </form>
      </Modal>
  )

  return (
    <>
      <HomeBar />
      <div id="containerOrders">
        <NavBar />
        <div id="alingOrders">
        {navClassifications}
        {listDish}
        </div>

      <input type="checkbox" id="command"hidden />
      <label htmlFor="command" className="command-icon"><p>Comanda</p> </label>

      <nav className="command-menu" id="command-list" >
        <ul>
        {theCommand}
        </ul>
      </nav>

      </div>
      {modal}
    </>
  )
}
