import React, { useState, useEffect, useMemo } from "react";
import api from "../../services/api"
import NavBar from "../../components/navBar";
import HomeBar from "../../components/homeBar";
import "./styles.css";

import direction from "../../assets/button/direction.svg";
import exclude from "../../assets/button/destroy.svg";

export default function Table() {
  const [tables, setTables]= useState([]);
const tableList=(
  <ul className="table-list">
      {tables.map(item => (
        <li key={item._id}>
          <span key={item._id}>
            Mesa: <strong>{item.number}</strong>
          </span>
          <span>
            Lugares: <strong>{item.vacancies }</strong>
          </span>
          <span>
            Disponibilidade: <strong>{item.availability=== true?"Disponivel":"Ocupada"}</strong>
          </span>
          <button className="direction" onClick={() => handleAvailability(item._id)}>
            <img src={direction} alt="direction" />
          </button>
          <button className="destroy" onClick={() => handleDestroy(item)}>
            <img src={exclude} alt="destroy" />
          </button>
        </li>
      ))}
    </ul>


)
  useEffect(() => {
   setTables(JSON.parse(localStorage.getItem("tables")));

  }, []);
  async function handleDestroy(table_id){
    const response = await api.delete(`table/${table_id}`)
    console.log(response)
    const index = tables.indexOf(table_id)
    tables.splice(index,1)
    console.log(tables)
    localStorage.setItem("tables", JSON.stringify( tables))
    setTables(tables)
  }
  function handleAvailability(table){
    table.availability===true?table.availability = false:table.availability = true
    const index = tables.indexOf(table._id)
    tables[index] = table
    localStorage.setItem("tables", JSON.stringify( tables))
    setTables(tables)
    console.log(tables)
  }

  return (

    <>
      <HomeBar />
      <div id="containerTable">
        <NavBar />
        <section>
          <div id="listTable" className="tableOne">
           {tableList}
          </div>
          <div id="createTable" className="tableThree"></div>
        </section>
      </div>
    </>
  );
}
