import React, { useState } from "react";
import "./App.css";
//importar imagens dos botões
import menu from "./assets/button/menu.svg";
import ingredients from "./assets/button/ingredients.svg";
import order from "./assets/button/order.svg";
import dish from "./assets/button/dish.svg";
import table from "./assets/button/table.svg";

//importar rotas
import Routes from "./routes";

export default function App({ history }) {
  return (
    <div className="App">
      <div id="navBar">
        <button id="profile"></button>

        <button id="order" onClick={history.push("/orders")}>
          <img src={order} alt="order" />
        </button>

        <button id="menu" onClick={history.push("/menu")}>
          <img src={menu} alt="menu" />
        </button>

        <button id="dish" onClick={history.push("/dish")}>
          <img src={dish} alt="dish" />
        </button>

        <button id="ingredients" onClick={history.push("/ingredients")}>
          <img src={ingredients} alt="ingredients" />
        </button>

        <button id="table" onClick={history.push("/table")}>
          <img src={table} alt="table" />
        </button>
      </div>
      <Routes />
    </div>
  );
}
