import React, { useState, useEffect, useMemo } from "react";
import api from "../../services/api"
import NavBar from "../../components/navBar";
import HomeBar from "../../components/homeBar";
import "./styles.css";

import direction from "../../assets/button/direction.svg";
import exclude from "../../assets/button/destroy.svg";

export default function Table() {
  const [tables, setTables]= useState([])
  const [tablesTrue, setTablesTrue] = useState([]);
  const [tablesFalse, setTablesFalse] = useState([])

  useEffect(() => {
    setTables(JSON.parse(localStorage.getItem("tables")));
    const T = []
    const F = []
    tables.map(item => { console.log(item);item.availability ? T.push(item): F.push(item) })
    console.log(T)
    console.log(F)
    setTablesTrue(T)
    setTablesFalse(F)
  }, []);

  async function handleDestroy(table_id){
    const response = await api.delete(`tables/${table_id}`)
    console.log(response.data)
  }
  function handleAvailability(table){
    table.availability?trueToFalse(table):falseToTrue(table)

    function trueToFalse(table){
      const index = tablesTrue.indexOf(table._id)
      const T = tablesTrue.splice(index, 1)
      const F = tablesFalse
      F.push(T[0])
      setTablesFalse(F)
      let all = []
      all.push(tablesTrue.map(item => item))
      all.push(tablesFalse.map(item => item))
      setTables(all)
      localStorage.setItem('tables', JSON.stringify(tables))

    }
    function falseToTrue(table){
      const index = tablesFalse.indexOf(table._id)
      const F = tablesFalse.splice(index, 1)
      const T = tablesTrue
      F.push(F[0])
      setTablesFalse(T)
      let all = []
      all.push(tablesTrue.map(item => item))
      all.push(tablesFalse.map(item => item))
      setTables(all)
      localStorage.setItem('tables', JSON.stringify(tables))

    }
  }
  return (
    <>
      <HomeBar />
      <div id="containerTable">
        <NavBar />
        <section>
          <div id="listTable" className="tableOne">
            {tablesTrue.map(item => (
              <>

                <span key={item._id}>
                  Mesa: <strong>{item.number}</strong>
                </span>
                <span>
                  Lugares: <strong>{item.vacancies}</strong>
                </span>
                <button className="exclude" onClick={()=>handleDestroy(item._id)}>
                  <img src={exclude} alt="excluir" />
                </button>
                <button className="direction" onClick={()=> handleAvailability(item)}>
                  <img src={direction} alt="Ocupar" />
                </button>
                <br />
              </>
            ))}
          </div>
          <div id="useTable" className="tableTwo"></div>
          {tablesFalse.map(item => (
              <>
                <button className="direction" onClick={()=> handleAvailability(item)}>
                  <img src={direction} alt="Ocupar" />
                </button>
                <span key={item._id}>
                  Mesa: <strong>{item.number}</strong>
                </span>
                <span>
                  Lugares: <strong>{item.vacancies}</strong>
                </span>
                <br />
              </>
            ))}
          <div id="createTable" className="tableThree"></div>
        </section>
      </div>
    </>
  );
}
